  //       .get(this.urlC + id)
  //       .toPromise()
  //       .then((data) => {
  //         console.log(data);
  //         resolve(data as Mete);
  //       })
  //       .catch((e) => {
  //         console.error(e);
  //         reject(e);
  //       });
  //   });

  delPacchetto(id: number) {
    console.log(`${this.urlDel}/${id}`);
    return this.http.delete(`${this.urlDel}/${id}`);
  }
}
