import { Component, OnInit } from "@angular/core";
import { CatalogoService } from "src/app/services/catalogo.service";

@Component({
  selector: "app-catalogo",
  templateUrl: "./catalogo.component.html",
  styleUrls: ["./catalogo.component.css"],
})
export class CatalogoComponent implements OnInit {
  constructor(private catalogoService: CatalogoService) {}
  ngOnInit(): void {
    this.catalogoService
      .getPacchetti()
      .subscribe((dati) => (this.pacchetto = dati));
    console.log(this.pacchetto);
  }
  pacchetto: any;

  onDeletePacchetto() {
    this.catalogoService
      .delPacchetto(this.pacchetto.id)
      .subscribe((data) => console.log(data));
  }
}
<div class="cat">
    <div class="card" *ngFor="let item of pacchetto">
        <div class="card__content">
          <p class="card__title">{{item.nome}}</p>
          <p class="card__title">{{item.meta}}</p>
          <p class="card__description">{{item.descrizione}}</p>
          <button (click)="onDeletePacchetto()">Elimina</button>
        </div>
      </div>
      
</div>
