import { Component } from "@angular/core";
import { Pacchetto } from "src/app/models/pacchetto.model";
import { Prenotazione } from "src/app/models/prenotazione.model";
import { PrenotazioneService } from "src/app/services/prenotazione.service";

@Component({
  selector: "app-prenotazione",
  templateUrl: "./prenotazione.component.html",
  styleUrls: ["./prenotazione.component.css"],
})
export class PrenotazioneComponent {
  numPartecipanti: number;
  nomeUtente: string;
  pacchetto: Pacchetto;
  convalida: boolean;

  constructor(private prenotazioneService: PrenotazioneService) {}
  onAddPrenotazione() {
    let nuovaPrenotazione: Prenotazione = new Prenotazione(
      this.numPartecipanti,
      this.nomeUtente,
      this.pacchetto,
      this.convalida
    );
    this.prenotazioneService
      .aggiungiPrenotazione(nuovaPrenotazione)
      .subscribe((data) => console.log(data));
  }
}
<div class="form">
    <form #aggiuntaPrenotazione="ngForm" (ngSubmit)="addPacchetto()">
        <label>Numero Partecipanti</label>
        <input type="number" name="numPartecipanti" [(ngModel)]="numPartecipanti" required>
        <label>Nome Utente</label>
        <input type="text" name="nomeUtente" [(ngModel)]="nomeUtente" required>
        <label for="mete">Pacchetto</label>
        <input type="text" name="pacchetto" id="meta" [(ngModel)]="pacchetto">
        <button type="submit">Conferma</button>
    </form>
</div>
